import React, { useState, useEffect } from 'react';

function ExecuteCommand() {
  const [command, setCommand] = useState('');
  const [output, setOutput] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const eventSource = new EventSource(`http://170.187.237.161:3001/execute-command?command=${encodeURIComponent(command)}`);

    eventSource.onmessage = (event) => {
      setOutput(prevOutput => prevOutput + event.data); // Append new output to previous output
    };

    eventSource.onerror = (error) => {
      setError('Error streaming output');
      console.error('Error streaming output:', error);
    };

    return () => {
      eventSource.close(); // Clean up EventSource when component unmounts
    };
  }, [command]); // Reconnect to SSE stream when command changes

  const handleExecute = () => {
    // Reset output and error when executing a new command
    setOutput('');
    setError('');

    // No need to make a request here since SSE will handle streaming
  };

  return (
    <div>
      <h2>Execute Command</h2>
      <div>
        <label>Command:</label>
        <input type="text" value={command} onChange={(e) => setCommand(e.target.value)} />
      </div>
      <button onClick={handleExecute}>Execute</button>
      {output && <div><pre>{output}</pre></div>}
      {error && <div>Error: {error}</div>}
    </div>
  );
}

export default ExecuteCommand;
